mapper.select:

select goods_id, goods_name, goods_price, goods_stock, goods_des, goods_date
from goods 
where goods_id = #{goods_id}

mapper.update:

update goods
set goods_name = #{goods_name}, goods_price = #{goods_price}, goods_stock = #{goods_stock}, goods_des = #{goods_des}
where goods_id = #{goods_id}


update goods
set fileName = #{fileName}
where goods_id = #{goods_id}
