mapper.select:

select qna_no, qna_title, qna_content, qna_writer, qna_writerDate, qna_count
from qna 
where qna_no = #{qna_no}

mapper.update:

update qna
set qna_count = qna_count + 1
where qna_no = #{qna_no}


update qna
set qna_title = #{qna_title}, qna_content = #{qna_content}, qna_writer = #{qna_writer}
where qna_no = #{qna_no} and qna_pw = #{qna_pw}
