mapper.select:

select image_no, image_title, image_content, image_writer, image_writerDate, fileName
from image 
where image_no = #{image_no}

mapper.update:

update image
set image_title = #{image_title}, image_content = #{image_content}, image_writer = #{image_writer}
where image_no = #{image_no}


update image
set fileName = #{fileName}
where image_no = #{image_no}
