mapper.select:

select qna_id, mem_id, qna_title, qna_content, qna_writerDate, qna_count
from qna 
where qna_id = #{qna_id}

mapper.update:

update qna
set qna_count = qna_count + 1
where qna_id = #{qna_id}


update qna
set qna_title = #{qna_title}, qna_content = #{qna_content}
where qna_id = #{qna_id}

mapper.select:

select qna_id, qna_title 
from qna 
where qna_id = #{qna_id}
