mapper.select:

		select *
		from reply
		where goods_id = #{goods_id}
		order by regDate desc  		
  		limit #{pageNum}, #{amount}


select count(*)
from reply
where goods_id = #{goods_id}


select avg(rating)
from reply
where goods_id = #{goods_id}

mapper.update:

update goods
set ratingAvg = #{ratingAvg}
where goods_id = #{goods_id}
